&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                    q.append(i)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                    cnt[i][&lt;span class=&#34;built_in&#34;&gt;ord&lt;/span&gt;(colors[i]) - &lt;span class=&#34;built_in&#34;&gt;ord&lt;/span&gt;(&lt;span class=&#34;string&#34;&gt;&amp;#x27;a&amp;#x27;&lt;/span&gt;)] += &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                    ans = &lt;span class=&#34;built_in&#34;&gt;max&lt;/span&gt;(ans, &lt;span class=&#34;built_in&#34;&gt;max&lt;/span&gt;(cnt[i]))&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                    seen += &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;span class=&#34;keyword&#34;&gt;if&lt;/span&gt; seen == n: &lt;span class=&#34;keyword&#34;&gt;return&lt;/span&gt; ans&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;span class=&#34;keyword&#34;&gt;else&lt;/span&gt;: &lt;span class=&#34;keyword&#34;&gt;return&lt;/span&gt; -&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
&lt;p&gt;时间复杂度: O(n + m),&lt;br /&gt;
空间复杂度: O(n + m).&lt;/p&gt;

