&lt;span class=&#34;line&#34;&gt;            &lt;span class=&#34;keyword&#34;&gt;for&lt;/span&gt; (&lt;span class=&#34;type&#34;&gt;const&lt;/span&gt; &lt;span class=&#34;type&#34;&gt;int&lt;/span&gt; j : &amp;#123;&lt;span class=&#34;number&#34;&gt;-1&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;&amp;#125;) &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                &lt;span class=&#34;type&#34;&gt;const&lt;/span&gt; &lt;span class=&#34;type&#34;&gt;int&lt;/span&gt; next = idx + j;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                &lt;span class=&#34;keyword&#34;&gt;if&lt;/span&gt; (next &amp;gt;= n || next &amp;lt; &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt; || seen[next]) &lt;span class=&#34;keyword&#34;&gt;continue&lt;/span&gt;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                &lt;span class=&#34;keyword&#34;&gt;if&lt;/span&gt; (j == &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;) &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                    &lt;span class=&#34;keyword&#34;&gt;auto&lt;/span&gt; it = rtxMap.&lt;span class=&#34;built_in&#34;&gt;lower_bound&lt;/span&gt;(next);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                    &lt;span class=&#34;keyword&#34;&gt;if&lt;/span&gt; (it != rtxMap.&lt;span class=&#34;built_in&#34;&gt;end&lt;/span&gt;()) &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                        &lt;span class=&#34;type&#34;&gt;const&lt;/span&gt; &lt;span class=&#34;type&#34;&gt;int&lt;/span&gt; x = it-&amp;gt;first;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                        it-&amp;gt;second = &lt;span class=&#34;built_in&#34;&gt;min&lt;/span&gt;(it-&amp;gt;second, height + x - idx);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                        pq.&lt;span class=&#34;built_in&#34;&gt;push&lt;/span&gt;(&amp;#123;it-&amp;gt;second, x&amp;#125;);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                    &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                &amp;#125; &lt;span class=&#34;keyword&#34;&gt;else&lt;/span&gt; &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                    &lt;span class=&#34;keyword&#34;&gt;auto&lt;/span&gt; it = rtxMap.&lt;span class=&#34;built_in&#34;&gt;lower_bound&lt;/span&gt;(idx);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                    &lt;span class=&#34;comment&#34;&gt;// 2 5&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                    &lt;span class=&#34;comment&#34;&gt;// next = 4&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                    &lt;span class=&#34;keyword&#34;&gt;if&lt;/span&gt; (it != rtxMap.&lt;span class=&#34;built_in&#34;&gt;begin&lt;/span&gt;()) &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                        --it;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                        &lt;span class=&#34;type&#34;&gt;const&lt;/span&gt; &lt;span class=&#34;type&#34;&gt;int&lt;/span&gt; x = it-&amp;gt;first;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                        it-&amp;gt;second = &lt;span class=&#34;built_in&#34;&gt;min&lt;/span&gt;(it-&amp;gt;second, height + idx - x);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                        pq.&lt;span class=&#34;built_in&#34;&gt;push&lt;/span&gt;(&amp;#123;it-&amp;gt;second, x&amp;#125;);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                    &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;span class=&#34;keyword&#34;&gt;for&lt;/span&gt; (&lt;span class=&#34;keyword&#34;&gt;auto&lt;/span&gt; it = rtxMap.&lt;span class=&#34;built_in&#34;&gt;begin&lt;/span&gt;(); &lt;span class=&#34;built_in&#34;&gt;next&lt;/span&gt;(it) != rtxMap.&lt;span class=&#34;built_in&#34;&gt;end&lt;/span&gt;(); ++it) &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            &lt;span class=&#34;type&#34;&gt;const&lt;/span&gt; &lt;span class=&#34;type&#34;&gt;int&lt;/span&gt; leftIdx = it-&amp;gt;first, leftHeight = it-&amp;gt;second;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            &lt;span class=&#34;type&#34;&gt;const&lt;/span&gt; &lt;span class=&#34;type&#34;&gt;int&lt;/span&gt; rightIdx = &lt;span class=&#34;built_in&#34;&gt;next&lt;/span&gt;(it)-&amp;gt;first, rightHeight = &lt;span class=&#34;built_in&#34;&gt;next&lt;/span&gt;(it)-&amp;gt;second;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            &lt;span class=&#34;keyword&#34;&gt;if&lt;/span&gt; (leftIdx + &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt; == rightIdx) &lt;span class=&#34;keyword&#34;&gt;continue&lt;/span&gt;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            &lt;span class=&#34;comment&#34;&gt;// cout &amp;lt;&amp;lt; leftIdx &amp;lt;&amp;lt; &amp;quot;, &amp;quot; &amp;lt;&amp;lt; leftHeight &amp;lt;&amp;lt; &amp;quot;, &amp;quot; &amp;lt;&amp;lt; rightIdx &amp;lt;&amp;lt; &amp;quot;, &amp;quot; &amp;lt;&amp;lt; rightHeight &amp;lt;&amp;lt; endl;&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            &lt;span class=&#34;type&#34;&gt;int&lt;/span&gt; lo = &lt;span class=&#34;built_in&#34;&gt;min&lt;/span&gt;(leftHeight, rightHeight), hi = &lt;span class=&#34;built_in&#34;&gt;min&lt;/span&gt;(leftHeight + rightIdx - leftIdx, rightHeight + rightIdx - leftIdx);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            &lt;span class=&#34;keyword&#34;&gt;while&lt;/span&gt; (lo &amp;lt; hi) &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                &lt;span class=&#34;type&#34;&gt;const&lt;/span&gt; &lt;span class=&#34;type&#34;&gt;int&lt;/span&gt; mid = lo + (hi - lo) / &lt;span class=&#34;number&#34;&gt;2&lt;/span&gt;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                &lt;span class=&#34;type&#34;&gt;bool&lt;/span&gt; deter = &lt;span class=&#34;literal&#34;&gt;false&lt;/span&gt;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                &lt;span class=&#34;keyword&#34;&gt;if&lt;/span&gt; (mid - leftHeight &amp;lt; rightIdx - leftIdx &lt;span class=&#34;keyword&#34;&gt;and&lt;/span&gt; mid - rightHeight &amp;lt; rightIdx - leftIdx &lt;span class=&#34;keyword&#34;&gt;and&lt;/span&gt; mid - leftHeight + mid - rightHeight - &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt; &amp;lt; rightIdx - leftIdx) deter = &lt;span class=&#34;literal&#34;&gt;true&lt;/span&gt;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                &lt;span class=&#34;keyword&#34;&gt;if&lt;/span&gt; (deter) &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                    lo = mid + &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                &amp;#125; &lt;span class=&#34;keyword&#34;&gt;else&lt;/span&gt; &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                    hi = mid;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                &lt;span class=&#34;comment&#34;&gt;// t, t, t, f, f&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            ans = &lt;span class=&#34;built_in&#34;&gt;max&lt;/span&gt;(ans, lo - &lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;span class=&#34;keyword&#34;&gt;return&lt;/span&gt; ans;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#125;;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
&lt;p&gt;&lt;code&gt;m = restrictions.length&lt;/code&gt;&lt;br /&gt;
时间复杂度: O(m log m),&lt;br /&gt;
空间复杂度: O(m).&lt;/p&gt;
&lt;p&gt;&lt;a href=&#34;https://leetcode-cn.com/problems/maximum-building-height/solution/zui-gao-jian-zhu-gao-du-by-leetcode-solu-axbb/910208&#34;&gt;零神题解&lt;/a&gt;里有2次扫描的算法。虽然时间复杂度是一样的，但实现比较简单，常数上会更好些。&lt;/p&gt;
